const getPageMargins = () => {
    return `@page { margin: ${marginTop} ${marginRight} ${marginBottom} ${marginLeft} !important; }`;
  };

  export const PrintOut = React.forwardRef(({date,name,loan,fee},ref) => {
    return (
        <div ref ={ref}>

            <div className="">
                <h1>Gisha Loans</h1>
            </div>
            <div>Date: <span>{date}</span></div>
            <div>Recieved From: <span>{name}</span></div>
            <div>Amount: <span>{parseInt(loan) + parseInt(fee)}</span></div>
            <div>Loan value: <span>{loan}</span></div>
            <div>Fee: <span>{fee}</span></div>
        </div>
    );
  });
