const date = format(new Date(), 'dd.MMM.YYYY HH:'); 

  export const PrintOut = React.forwardRef(({date,name,loan,fee},ref) => {
    return (
        <div ref ={ref} className="print-container w-full" style={{ margin: "0", padding: "20" }}>
            <div className="float-right">
                <h1 className='w-full text-2xl font-bold'>Gisha Loans</h1>
                <p className="">Kampala UG</p>
                <p className="">+256 752573326 / +256 761074816</p>
                <p className="">{date}</p>
            </div>
            <div>Date: <span>{date}</span></div>
            <div>Recieved From: <span>{name}</span></div>
            <div>Amount: <span>{parseInt(loan) + parseInt(fee)}</span></div>
            <div>Loan value: <span>{loan}</span></div>
            <div>Fee: <span>{fee}</span></div>
        </div>
    );
  });
