import React from 'react'
import { format } from 'fecha';
// export const PrintOut = React.forwardRef(({date,name,loan,fee}),ref) =>{
//   return (

//   )
// };
const date = format(new Date(), 'dd/MMM/YYYY hh:mm:ss'); 

  export const PrintOut = React.forwardRef(({name,loan,fee},ref) => {
    return (
        <div ref ={ref} className="print-container w-full" style={{ margin: "0", padding: "20" }}>
            <div className="">
                <h1 className='w-full text-2xl font-bold'>Gisha Loans</h1>
                <p className="font-semibold text-lg">Kalerwe Market</p>
                <p className="font-semibold text-lg">+256 702 939679</p><p className='font-semibold text-lg'> +256 780 569011</p>
                <p className="mt-4">Date: {date}</p>
            </div>
            <div className="mt-8">
                <h1 className="underline text-4xl text-center w-full mt-8 mb-4">LOAN DETAILS</h1>
                <div>I <span className='underline'>{name}</span> have recieved a loan of value <span>{loan}</span> from</div>
                <div>Amount: <span>{parseInt(loan) + parseInt(fee)}</span></div>
                <div>Loan value: <span>{loan}</span></div>
                <div>Fee: <span>{fee}</span></div>
                </div>
        </div>
    );
  });
